INSERT INTO food_groups (id, name, description) VALUES (1, 'Vegetables', 'All raw vegetables, eg lettuce, cabbage, carrot, tomato, cucumber, onion, etc.\nAll cooked vegetables, eg broccoli, cauliflower, courgettes, greens, beetroot etc.\nStarchy vegetables, eg, peas, corn, pumpkin\nPotato and its varieties are NOT included');
INSERT INTO food_groups (id, name, description) VALUES (2, 'Fruits', 'All raw fruits, e.g., orange, apple, pear, banana, peach, etc.\nAll dried fruits, e.g., plums, raisins, apricots, etc.\nNatural fruit juices (100% without added sugar).');
INSERT INTO food_groups (id, name, description) VALUES (3, 'Cereals (bread, rice, pasta) and Potatoes', 'Wheat, oats, barley, rye etc.\nRice\nFlour\nBread\nSimple baked goods, eg, toasts, nuts, breadcrumbs, crackers\nComplex pastries, eg, doughs, pies\nPasta, eg, spaghetti, barley, noodles\nVarious cereal products, e.g., oatmeal, bran\nCereal');
INSERT INTO food_groups (id, name, description) VALUES (4, 'Milk and milk products', 'The milk\nDairy products, e.g., yogurt, cheese, sour milk, etc.\nButter is NOT included (included in fats and oils)');
INSERT INTO food_groups (id, name, description) VALUES (5, 'Legumes', 'The lentils\nThe beans\nThe chickpeas\nThe fava bean\nThe dry beans\nVarieties of all of the above');
INSERT INTO food_groups (id, name, description) VALUES (6, 'Red meat', 'Veal, beef\nPork\nLamb, sheep\nGoat, goat\nGame: e.g., wild boar, deer, deer\nAll processed products of the above');
INSERT INTO food_groups (id, name, description) VALUES (7, 'White meat', 'Chicken\nTurkey\nDuck\nRabbit\nGame: eg, pheasant, quail, partridge\nAll processed products of the above');
INSERT INTO food_groups (id, name, description) VALUES (8, 'Eggs', '');
INSERT INTO food_groups (id, name, description) VALUES (9, 'Fish and seafood', 'Fish, e.g. sardine, mullet, goby, anchovy, atherina, ruff, flounder, grouper, cod, galley, tuna, sea bass, bream, bream, red snapper\nSeafood (molluscs, shellfish, crustaceans), eg squid, cuttlefish, octopus, shrimp, mussels, oysters.');
INSERT INTO food_groups (id, name, description) VALUES (10, 'Added fats and oils, olives and nuts', 'Added fats and oils:\n\n    Olive oil\n    Other vegetable oils (seed oils): sunflower oil, corn oil, soybean oil, sesame oil, etc.\n    Margarine\n    Butter\nThe olives\nNuts\n\n    Walnuts, almonds, peanuts, hazelnuts, etc.\n    Sunflower seeds, sesame etc.\n    Spreads derived from the above (eg tahini)');
