INSERT INTO themes (id, name, description) VALUES (1, 'International Street Food Fusion', 'Take your taste buds on a journey around the world with this theme, where contestants blend flavors from different cuisines to create unique street food dishes that reflect global culinary influences.');
INSERT INTO themes (id, name, description) VALUES (2, 'Comfort Food Classics', 'In this theme, contestants revisit beloved comfort food recipes, putting their own twist on classic dishes that evoke feelings of nostalgia and warmth.');
INSERT INTO themes (id, name, description) VALUES (3, 'Spice Challenge: Hot and Spicy Creations', 'Brace yourself for a fiery competition where contestants showcase their ability to handle heat by incorporating bold spices and peppers into their dishes, delivering intense flavor experiences.');
INSERT INTO themes (id, name, description) VALUES (4, 'Garden-to-Table Freshness', 'Celebrate the freshness of seasonal produce with this theme, where contestants source ingredients straight from the garden to create vibrant, flavorful dishes that highlight the beauty of natures bounty.');
INSERT INTO themes (id, name, description) VALUES (5, 'Sweet Treats Showdown', 'Indulge your sweet tooth with this theme, where contestants compete to create decadent desserts and confections that tantalize the taste buds and satisfy sugar cravings.');
INSERT INTO themes (id, name, description) VALUES (6, 'Mystery Ingredient Madness', 'Test the culinary prowess of contestants with this challenge, where they must incorporate surprise mystery ingredients into their dishes, showcasing their creativity and adaptability under pressure.');
INSERT INTO themes (id, name, description) VALUES (7, 'Healthy Twist: Low-Calorie Creations', 'Encourage contestants to get creative with nutritious ingredients in this theme, challenging them to craft delicious low-calorie dishes that prioritize health without compromising on flavor.');
INSERT INTO themes (id, name, description) VALUES (8, 'Retro Revival: Vintage Recipes Reimagined', 'Give classic recipes a modern makeover in this theme, where contestants breathe new life into retro dishes from the past, infusing them with contemporary flair and innovation.');
INSERT INTO themes (id, name, description) VALUES (9, 'Around the World in 80 Dishes', 'Embark on a culinary journey across continents with this theme, as contestants explore diverse cuisines and cultural traditions to create an eclectic array of dishes inspired by global travel.');
INSERT INTO themes (id, name, description) VALUES (10, 'Family Recipe Feud', 'Put family recipes to the test in this theme, where contestants draw inspiration from cherished family culinary traditions to create dishes that honor their heritage and evoke memories of home-cooked meals.');
INSERT INTO themes (id, name, description) VALUES (11, 'Gourmet Grilled Cheese Galore', 'Elevate the humble grilled cheese sandwich to gourmet status with this theme, as contestants experiment with different breads, cheeses, and fillings to create mouthwatering variations of this comfort food classic.');
INSERT INTO themes (id, name, description) VALUES (12, 'Breakfast Battle: Morning Meal Masterpieces', 'Start the day off right with this theme, where contestants showcase their breakfast-making skills by crafting creative and delicious morning meal masterpieces that are sure to impress.');
INSERT INTO themes (id, name, description) VALUES (13, 'Seafood Sensation Showcase', 'Dive into a sea of flavors with this theme, as contestants reel in the competition by preparing sensational seafood dishes that highlight the freshness and versatility of ocean-inspired ingredients.');
INSERT INTO themes (id, name, description) VALUES (14, 'Vegan Delights Duel', 'Challenge contestants to think outside the box with this theme, where they must create plant-based dishes that are both delicious and innovative, showcasing the endless possibilities of vegan cuisine.');
INSERT INTO themes (id, name, description) VALUES (15, 'Pasta Palooza: Italian Pasta Extravaganza', 'Celebrate the culinary heritage of Italy with this theme, as contestants compete to create mouthwatering pasta dishes that showcase a variety of shapes, flavors, and sauces.');
INSERT INTO themes (id, name, description) VALUES (16, 'BBQ Bonanza: Grilling Greatness', 'Fire up the grill and get ready for a sizzling showdown in this theme, where contestants showcase their mastery of barbecue techniques and flavor profiles to create irresistible grilled dishes.');
INSERT INTO themes (id, name, description) VALUES (17, 'Appetizer Adventure: Bite-Sized Bites', 'Kick off the meal with a tantalizing array of appetizers in this theme, as contestants compete to create bite-sized bites that pack a big punch of flavor and leave guests craving more.');
INSERT INTO themes (id, name, description) VALUES (18, 'Chocolate Craze: Decadent Desserts', 'Indulge in a chocolate lovers dream with this theme, where contestants craft decadent desserts that showcase the rich and luxurious flavors of chocolate in all its forms.');
INSERT INTO themes (id, name, description) VALUES (19, 'Ingredient Swap Challenge: Substitutions Showdown', 'Put contestants culinary skills to the test with this challenge, where they must think on their feet and adapt recipes on the fly by incorporating unexpected ingredient substitutions.');
