INSERT INTO themes (id, name, description) VALUES (23, 'Farm-to-Table Feast: Locally Sourced Ingredients', 'Highlight the importance of sustainable eating with this theme, as contestants use locally sourced ingredients to create farm-to-table dishes that celebrate the flavors of the season.');
